<script>

import axios from "axios";
import {upuser_pic} from '~/plugins/index';
import {nomessages} from '~/plugins/index';
export default {
    auth:true,
    props: ['message_info','verify','user_pic'],
    name: "Left",
    data() {
        return {
            //当前路由
            changeSelectStyle: this.$route.path,
            // 头像图片
            imageUrl: '',
            pic: '',
            number: '0',
            days: this.DateDiffer(this.$store.state.auth.user.created_at),
            is_vip: false,
            auths: {},
        }
    },

    async created() {
        if (this.$store.state.auth.user != null) {
            const news = await nomessages();
            this.number = news.num;
            this.auths = {Authorization: "Bearer " + this.$store.state.auth.user.api_token};
            this.days = this.DateDiffer(this.$store.state.auth.user.created_at);
        } else {
            this.pic = '';
        }
    },
    methods: {
        DateDiffer(Date_end) {
            //date1结束时间
            let date1 = new Date(Date_end);
            //date2当前时间
            let date2 = new Date();
            date1 = new Date(date1.getFullYear(), date1.getMonth(), date1.getDate());
            date2 = new Date(date2.getFullYear(), date2.getMonth(), date2.getDate());
            const diff = date2.getTime() - date1.getTime(); //目标时间减去当前时间
            const diffDate = diff / (24 * 60 * 60 * 1000);  //计算当前时间与结束时间之间相差天数
            return diffDate;
        },
        goPerson() {
            this.$router.push('/member')
        },
        Vips() {
            this.$router.push('/member/vip')
        },
        deposit() {
            this.$router.push('/member/deposit')
        },
        transfer() {
            this.$router.push('/member/transfer')
        },
        withdrawals() {
            this.$router.push('/member/withdrawals')
        },
        order() {
            this.$router.push('/member/order')
        },
        wallet() {
            this.$router.push('/member/wallet')
        },
        newsfav() {
            this.$router.push('/member/newsfav')
        },
        mycollect() {
            this.$router.push('/member/mycollect')
        },
        message() {
            this.$router.push('/member/message')
        },
        suggestion() {
            this.$router.push('/member/suggestion')
        },
        verifys(){
            this.$router.push('/member/verify')
        },
        chat(){
            this.$router.push('/member/chat')
        },
        handleAvatarSuccess(res, file) {
            this.user_pic = res.files.file;
            if (res.status == 200) {
                this.$message.success(res.msg)
            }
        },
        beforeAvatarUpload(file) {
            const isJPG = file.type === 'image/jpeg' || file.type === 'image/jpg' || file.type === 'image/png' || file.type === 'image/PNG' || file.type === 'image/JPG';
            const isLt2M = file.size / 1024 / 1024 < 2;
            if (!isJPG) {
                this.$message.error("上传头像图片只能是 JPG或PNG 格式!");
                return isJPG && isLt2M;
            }
            if (!isLt2M) {
                this.$message.error("上传头像图片大小不能超过 2MB!");
                return isJPG && isLt2M;
            }
            this.limt += 1;
        },
        async onSubmit() {
            this.$refs.upload.submit();
        },
        async upload() {
            const questions = await upuser_pic(this.imageUrl);
            this.user_pic = questions.imgpath;
        }
    }
}
</script>

